import axios from 'axios';

//◆全削除
 export async function deleteAllSelected(url) {
      
    if (!this.multipleSelection || this.multipleSelection.length === 0) {
      this.$message.warning("削除する明細を選択してください。");
      return;
    }

    try {
      const response = await axios.post(url, {
        employees: this.multipleSelection
      });
      
      if (response.data.success) {
        this.$message.success("選択された明細が削除されました。");
        this.findEmployees(); // データを再取得
      } else {
        this.$message.error("削除に失敗しました。");
      }

    } catch (error) {
      this.$message.error("エラーが発生しました: " + error);
    }
  }
